package controller;import dao.exception.DAOException;import dao.exception.ServiceException;import dao.factory.DAOFactory;import dao.implement.CarDAO;import dao.implement.TestDriveDAO;import entity.Car;import entity.TestDrive;import service.AddService;import service.DeleteService;import service.UpdateService;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.List;/** * Created by evgen on 08.04.2016. */public class TestDriveController extends HttpServlet{    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        TestDrive testDrive;        switch (request.getParameter("request")){            case "add_testDrive":                testDrive = new TestDrive(request.getParameter("mark"),                        request.getParameter("model"),                        request.getParameter("date"));                try {                    AddService.add(testDrive,"testDrive");                } catch (ServiceException e) {                }                break;            case "delete_testDrive":                testDrive = new TestDrive();                testDrive.setIdTestDrive(Integer.parseInt(request.getParameter("idTestDriveOnDelete")));                try {                    DeleteService.delete(testDrive,"testDrive");                } catch (ServiceException e) {                }                break;            case "update_testDrive":                testDrive = new TestDrive(request.getParameter("mark"),                        request.getParameter("model"),                        request.getParameter("date"));                testDrive.setIdTestDrive(Integer.parseInt(request.getParameter("idTestDriveOnDelete")));                try {                    UpdateService.update(testDrive,"testDrive");                } catch (ServiceException e) {                }                break;        }    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        /*TestDriveDAO dao = DAOFactory.getTestDrive();        try {            List<TestDrive> testDriveList = dao.getAll();            request.setAttribute("testDriveList", testDriveList);            RequestDispatcher view = request.getRequestDispatcher("/mechanic.jsp");   /////////////////            view.forward(request, response);        } catch (DAOException e) {            RequestDispatcher view = request.getRequestDispatcher("/error.jsp");            view.forward(request,response);        }*/    }}